import React, { useRef, useMemo } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Sphere, OrbitControls, Environment } from '@react-three/drei';
import * as THREE from 'three';
import './Planet3D.css';

interface Planet3DProps {
  color?: string;
  size?: number;
  lightIntensity?: number;
  lightColor?: string;
  rotationSpeed?: number;
  wireframe?: boolean;
  metalness?: number;
  roughness?: number;
  craterDensity?: number;
  mountainHeight?: number;
}

const PlanetMesh: React.FC<Planet3DProps> = ({ 
  color = '#4a90e2', 
  size = 1, 
  rotationSpeed = 1,
  wireframe = false,
  metalness = 0.1,
  roughness = 0.8,
  craterDensity = 0.3,
  mountainHeight = 0.1
}) => {
  const meshRef = useRef<THREE.Mesh>(null);

  // Generar geometría procedural con solo cráteres y montañas
  const geometry = useMemo(() => {
    const sphereGeometry = new THREE.SphereGeometry(size, 128, 128);
    const positions = sphereGeometry.attributes.position.array as Float32Array;
    const normals = sphereGeometry.attributes.normal.array as Float32Array;

    // Crear montañas específicas
    const mountainCount = Math.floor(mountainHeight * 10);
    const mountains: Array<{x: number, y: number, z: number, height: number, radius: number}> = [];

    for (let m = 0; m < mountainCount; m++) {
      const phi = Math.random() * Math.PI;
      const theta = Math.random() * Math.PI * 2;
      const x = Math.sin(phi) * Math.cos(theta);
      const y = Math.cos(phi);
      const z = Math.sin(phi) * Math.sin(theta);

      mountains.push({
        x, y, z,
        height: 0.05 + Math.random() * 0.1,
        radius: 0.2 + Math.random() * 0.3
      });
    }

    // Crear cráteres específicos
    const craterCount = Math.floor(craterDensity * 15);
    const craters: Array<{x: number, y: number, z: number, depth: number, radius: number}> = [];

    for (let c = 0; c < craterCount; c++) {
      const phi = Math.random() * Math.PI;
      const theta = Math.random() * Math.PI * 2;
      const x = Math.sin(phi) * Math.cos(theta);
      const y = Math.cos(phi);
      const z = Math.sin(phi) * Math.sin(theta);

      craters.push({
        x, y, z,
        depth: 0.02 + Math.random() * 0.08,
        radius: 0.15 + Math.random() * 0.25
      });
    }

    // Aplicar solo montañas y cráteres
    for (let i = 0; i < positions.length; i += 3) {
      const x = positions[i];
      const y = positions[i + 1];
      const z = positions[i + 2];

      let totalEffect = 0;

      // Aplicar montañas
      for (const mountain of mountains) {
        const distance = Math.sqrt(
          Math.pow(x - mountain.x, 2) + 
          Math.pow(y - mountain.y, 2) + 
          Math.pow(z - mountain.z, 2)
        );

        if (distance < mountain.radius) {
          const mountainEffect = (mountain.radius - distance) / mountain.radius * mountain.height;
          totalEffect += mountainEffect;
        }
      }

      // Aplicar cráteres
      for (const crater of craters) {
        const distance = Math.sqrt(
          Math.pow(x - crater.x, 2) + 
          Math.pow(y - crater.y, 2) + 
          Math.pow(z - crater.z, 2)
        );

        if (distance < crater.radius) {
          const craterEffect = (crater.radius - distance) / crater.radius * crater.depth;
          totalEffect -= craterEffect;
        }
      }

      // Aplicar el efecto a la posición
      const normalX = normals[i];
      const normalY = normals[i + 1];
      const normalZ = normals[i + 2];

      positions[i] += normalX * totalEffect;
      positions[i + 1] += normalY * totalEffect;
      positions[i + 2] += normalZ * totalEffect;
    }

    sphereGeometry.attributes.position.needsUpdate = true;
    sphereGeometry.computeVertexNormals();

    return sphereGeometry;
  }, [size, craterDensity, mountainHeight]);

  useFrame((state, delta) => {
    if (meshRef.current) {
      meshRef.current.rotation.y += delta * rotationSpeed;
    }
  });

  return (
    <mesh ref={meshRef} geometry={geometry}>
      <meshStandardMaterial
        color={color}
        wireframe={wireframe}
        metalness={metalness}
        roughness={roughness}
        normalScale={new THREE.Vector2(0.5, 0.5)}
      />
    </mesh>
  );
};

const Planet3D: React.FC<Planet3DProps> = ({
  color = '#4a90e2',
  size = 1,
  lightIntensity = 1,
  lightColor = '#ffffff',
  rotationSpeed = 1,
  wireframe = false,
  metalness = 0.1,
  roughness = 0.8,
  craterDensity = 0.3,
  mountainHeight = 0.1
}) => {
  return (
    <div className="planet-container">
      <Canvas
        camera={{ position: [0, 0, 5], fov: 75 }}
        style={{ width: '100%', height: '500px' }}
      >
        <ambientLight intensity={0.2} />
        <directionalLight
          position={[10, 10, 5]}
          intensity={lightIntensity}
          color={lightColor}
        />
        <pointLight position={[-10, -10, -5]} intensity={0.5} color={lightColor} />

        <PlanetMesh
          color={color}
          size={size}
          rotationSpeed={rotationSpeed}
          wireframe={wireframe}
          metalness={metalness}
          roughness={roughness}
          craterDensity={craterDensity}
          mountainHeight={mountainHeight}
        />

        <OrbitControls
          enablePan={true}
          enableZoom={true}
          enableRotate={true}
          autoRotate={false}
        />

        <Environment preset="night" />
      </Canvas>
    </div>
  );
};

export default Planet3D;
