import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import Header from '../components/Header';
import Planet3D from '../components/Planet3D';
import Planet3DGLB from '../components/Planet3DGLB';
import './Home.css';

const Home: React.FC = () => {
  const [selectedPlanet, setSelectedPlanet] = useState<'moon' | 'jupiter'>('moon');
  const [planetSize, setPlanetSize] = useState(0.6);


  return (
    <div className="home">
      <Navbar />
      <Header />

      <section className="planet-section">
        <div className="container">
          <h2 className="section-title">Planeta 3D Interactivo</h2>
          <p className="section-description">
            Explora y personaliza tu propio planeta con diferentes configuraciones
          </p>

          <div className="planet-showcase">
            <div className="planet-controls">
              <div className="control-group">
                <label htmlFor="size">Tama√±o: {planetSize}</label>
                <input
                  type="range"
                  id="size"
                  min="0.5"
                  max="2"
                  step="0.1"
                  value={planetSize}
                  onChange={(e) => setPlanetSize(parseFloat(e.target.value))}
                />
              </div>
            </div>

            <div className="planet-display">
              <Planet3DGLB 
                planetType={selectedPlanet}
                size={planetSize}
                rotationSpeed={0.5}
                autoRotate={true}
                shadowIntensity={1}
                cameraOrbit="120deg -30deg 25m"
              />
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
