import React, { useEffect, useRef } from 'react';
import './Planet3D.css';

interface Planet3DGLBProps {
  planetType?: 'moon' | 'jupiter';
  size?: number;
  rotationSpeed?: number;
  autoRotate?: boolean;
  shadowIntensity?: number;
  environmentImage?: string;
}

const Planet3DGLB: React.FC<Planet3DGLBProps> = ({
  planetType = 'moon',
  size = 1,
  rotationSpeed = 1,
  autoRotate = true,
  shadowIntensity = 1,
  environmentImage
}) => {
  const modelViewerRef = useRef<HTMLElement>(null);

  const getPlanetPath = (planet: string) => {
    switch (planet) {
      case 'moon':
        return '/glb_planets/Moon.glb';
      case 'jupiter':
        return '/glb_planets/Jupiter.glb';
      default:
        return '/glb_planets/Moon.glb';
    }
  };

  useEffect(() => {
    // Cargar el script de model-viewer si no está cargado
    if (!document.querySelector('script[src*="model-viewer"]')) {
      const script = document.createElement('script');
      script.type = 'module';
      script.src = 'https://ajax.googleapis.com/ajax/libs/model-viewer/4.0.0/model-viewer.min.js';
      document.head.appendChild(script);
    }
  }, []);

  useEffect(() => {
    if (modelViewerRef.current) {
      const modelViewer = modelViewerRef.current as any;

      // Configurar rotación automática
      if (autoRotate) {
        modelViewer.setAttribute('auto-rotate', '');
        modelViewer.setAttribute('auto-rotate-delay', '0');
      }

      // Configurar velocidad de rotación
      if (rotationSpeed !== 1) {
        modelViewer.setAttribute('rotation-per-second', `${rotationSpeed}rad`);
      }
    }
  }, [autoRotate, rotationSpeed]);

  return (
    <div className="planet-container">
      <div
        ref={modelViewerRef}
        dangerouslySetInnerHTML={{
          __html: `
            <model-viewer
              src="${getPlanetPath(planetType)}"
              alt="${planetType} 3D model"
              style="width: 100%; height: 500px; --poster-color: transparent;"
              camera-controls
              touch-action="pan-y"
              shadow-intensity="${shadowIntensity}"
              ${environmentImage ? `environment-image="${environmentImage}"` : ''}
              min-camera-orbit="auto auto 1.5"
              max-camera-orbit="auto auto 10"
              field-of-view="45deg"
              camera-target="0m 0m 0m"
              interaction-policy="allow-when-focused"
              ${autoRotate ? 'auto-rotate' : ''}
              ${autoRotate ? 'auto-rotate-delay="0"' : ''}
              ${rotationSpeed !== 1 ? `rotation-per-second="${rotationSpeed}rad"` : ''}
            ></model-viewer>
          `
        }}
      />
    </div>
  );
};

export default Planet3DGLB;
