import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import Header from '../components/Header';
import Planet3D from '../components/Planet3D';
import Planet3DGLB from '../components/Planet3DGLB';
import './Home.css';

const Home: React.FC = () => {
  const [selectedPlanet, setSelectedPlanet] = useState<'moon' | 'jupiter'>('moon');
  const planetSize = 0.8;

  // Configuraci칩n de c치mara espec칤fica para cada planeta
  const getCameraSettings = (planet: 'moon' | 'jupiter') => {
    switch (planet) {
      case 'jupiter':
        return {
          cameraOrbit: "60deg -15deg 50m",
          rotationSpeed: 0.2
        };
      case 'moon':
      default:
        return {
          cameraOrbit: "60deg -15deg 30m",
          rotationSpeed: 0.3
        };
    }
  };

  const cameraSettings = getCameraSettings(selectedPlanet);


  return (
    <div className="home">
      <Navbar />
      <Header />

      <section className="planet-section">
        <div className="container">
          <h2 className="section-title">Planeta 3D Interactivo</h2>
          <p className="section-description">
            Explora y personaliza tu propio planeta con diferentes configuraciones
          </p>

          <div className="planet-showcase">
            <div className="planet-selection">
              <h3>Selecciona un Planeta</h3>
              <div className="radio-group">
                <label className="radio-option">
                  <input
                    type="radio"
                    name="planet"
                    value="moon"
                    checked={selectedPlanet === 'moon'}
                    onChange={(e) => setSelectedPlanet(e.target.value as 'moon' | 'jupiter')}
                  />
                  <span className="radio-custom"></span>
                  <span className="radio-label">游깿 Luna</span>
                </label>
                <label className="radio-option">
                  <input
                    type="radio"
                    name="planet"
                    value="jupiter"
                    checked={selectedPlanet === 'jupiter'}
                    onChange={(e) => setSelectedPlanet(e.target.value as 'moon' | 'jupiter')}
                  />
                  <span className="radio-custom"></span>
                  <span className="radio-label">游뿛 J칰piter</span>
                </label>
              </div>
            </div>

            <div className="planet-display">
              <Planet3DGLB 
                planetType={selectedPlanet}
                size={planetSize}
                rotationSpeed={cameraSettings.rotationSpeed}
                autoRotate={true}
                shadowIntensity={1.2}
                cameraOrbit={cameraSettings.cameraOrbit}
              />
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
